import { useContext, useEffect, useState } from 'react';
import { UserContext } from '../userContext';

function Home(){
    const userContext = useContext(UserContext);
    const [location, setLocation] = useState(null);
    const [weather, setWeather] = useState(null);

    useEffect(() => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition((position) => {
                const { latitude, longitude } = position.coords;
                setLocation({ latitude, longitude });

                fetch(`http://api.openweathermap.org/data/2.5/onecall?lat=${latitude}&lon=${longitude}&exclude=minutely,hourly,daily,alerts&appid=a079118662ea266c81754b94e99980ab`)
                .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                return response.json();
                })
                .then(data => setWeather(data))
                .catch(error => {
                    console.error('There was a problem with the fetch operation: ', error);
                });
            }, (error) => {
                console.error("Error Code = " + error.code + " - " + error.message);
            });
        }
    }, []);

    let tempCelsius;
    if (weather && weather.current) {
        tempCelsius = weather.current.temp - 273.15;
    }

    return (
        <>
            {location && <p>Your location: {location.latitude}, {location.longitude}</p>}
            {weather && weather.current && <p>Current temperature: {tempCelsius.toFixed(2)}</p>}
            {weather && weather.current && weather.current.weather && <p>Weather: {weather.current.weather[0].description}</p>}
        </>
    );
}

export default Home;
